package runner

import "testing"

func intPtr(value int) *int {
	return &value
}

func TestSelectFirstOpenLeafTaskIDNestedEpic(t *testing.T) {
	root := Issue{
		ID:        "epic-root",
		IssueType: "epic",
		Status:    "open",
		Children: []Issue{
			{
				ID:        "epic-1",
				IssueType: "epic",
				Status:    "open",
				Priority:  intPtr(1),
				Children: []Issue{
					{
						ID:        "task-closed",
						IssueType: "task",
						Status:    "closed",
						Priority:  intPtr(1),
					},
					{
						ID:        "epic-1-1",
						IssueType: "epic",
						Status:    "open",
						Priority:  intPtr(2),
						Children: []Issue{
							{
								ID:        "task-nested",
								IssueType: "task",
								Status:    "open",
								Priority:  intPtr(1),
							},
						},
					},
				},
			},
			{
				ID:        "task-root",
				IssueType: "task",
				Status:    "open",
				Priority:  intPtr(2),
			},
		},
	}

	leafID := SelectFirstOpenLeafTaskID(root)
	if leafID != "task-nested" {
		t.Fatalf("expected nested task-nested, got %q", leafID)
	}
}

